import React, {Component} from 'react';

import {withStyles} from "@material-ui/core/styles"
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import TextField from '@material-ui/core/TextField';

import {addNewTask} from "../../store/actions";

import {connect} from "react-redux";
import {bindActionCreators} from "redux";


const styles = theme => ({
    root:{
        flexGrow:1,
    },
    container:{
        justifyContent:"center",
    },
    paper:{
        padding:theme.spacing.unit * 2,
        textAlign:"center"
    }
});

const submitHandler = (e,addNewTask) => {
    e.preventDefault();
    console.log("submitHandler",e.target[0].value);
    addNewTask(e.target[0].value);
    e.target.reset();
};

class AddTask  extends Component{
render(){
    {
        const {classes,addNewTask} = this.props;
        return(
            <div className={classes.root}>
                <Grid container spacing={24} className={classes.container}>
                    <Grid item xs={10} sm={8} md={6}>
                        <Paper className={classes.paper}>
                            <form action="" onSubmit={(event)=>{
                                submitHandler(event, addNewTask)
                            }}>
                                <input type="text"/>
                                <TextField
                                    id="outlined-name"
                                    label="Enter new task"
                                    className={classes.textField}
                                    value={this.state.task}
                                    onChange={this.handleChange('name')}
                                    margin="normal"
                                    variant="outlined"
                                />
                                <button>Add task</button>
                            </form>
                        </Paper>
                    </Grid>
                </Grid>
            </div>
        )
    };
}
}

const putStateToProps = (state) => {
    return {
        ...state
    }
};

const putActionsToComponent = (dispatch) => {
    return {
        addNewTask: bindActionCreators(addNewTask,dispatch)
    }
};

export default withStyles(styles)(connect(
    putStateToProps,
    putActionsToComponent
)(AddTask));
