import {ADD_NEW_TASK, DONE_TASK} from "../index";

export const addNewTask = (task) => {
    return {
        type: ADD_NEW_TASK,
        payload: task
    }
};

export const doneTask = (task_id) => {
    return {
        type: DONE_TASK,
        payload: task_id
    }
};

export const removeTask = (task_id) => {
    return {
        type: DONE_TASK,
        payload: task_id
    }
};
