import React from 'react';

import {withStyles} from "@material-ui/core/styles"
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";

import {addNewTask} from "../../store/actions";

import {connect} from "react-redux";
import {bindActionCreators} from "redux";


const styles = theme => ({
    root:{
        flexGrow:1,
    },
    paper:{

    }
});

const submitHandler = (e,addNewTask) => {
    e.preventDefault();
    console.log("submitHandler",e.target[0].value);
    addNewTask(e.target[0].value);
    e.target.reset();
}

const AddTask = props =>{
    const {classes,addNewTask} = props;
    return(
    <div className={classes.root}>
        <Grid container spacing={24}>
            <Grid item xs={12} md={6}>
                <Paper classes={classes.paper}>
                    <form action="" onSubmit={(event)=>{
                        submitHandler(event, addNewTask)
                    }}>
                        <input type="text"/>
                        <button>Add task</button>
                    </form>
                </Paper>
            </Grid>
        </Grid>
    </div>
    )
};

const putStateToProps = (state) => {
    return {
        ...state
    }
};

const putActionsToComponent = (dispatch) => {
    return {
        addNewTask: bindActionCreators(addNewTask,dispatch)
    }
};

export default withStyles(styles)(connect(
    putStateToProps,
    putActionsToComponent
)(AddTask));
